import React, { useContext, useEffect, useState } from "react";
import GenderGalleryItem from "./GenderGalleryItem"
import { PagesContext } from "../../Context/PagesProvider";
import { NavLink } from 'react-router-dom'
import { GetAxios, PatchAxios } from '../../Fetch/Fetching'
function GenderGallery({ gallery }) {


   console.log("gall: ", gallery);

   async function han(userID, basLikOrd, delProductID) {
      const response = await GetAxios(`UserChange?user_id=${userID}`) //get
      const respChange = await response[0][`${basLikOrd}`] // если Change если user то response[0]
      const respID = await response[0].id
      const filterChange = respChange.filter((item) => item.id != delProductID) // если удалить выбераем конкретный обьект в массиве
      console.log(filterChange);
      const body = { likes: filterChange } // ключи нужно проверять перебором if другого способа не вижу
      const responsePATCH = await PatchAxios(`UserChange/${respID}`, body) // обычный PATCH просто меняеться BODY
      console.log(responsePATCH);
   }

   return (
      <div class="gallery">
         <div class="container">
            <div class="gallery__title">Выберите категорию</div>
            <div class="gallery__block">
               {gallery.map((item) => {
                  console.log(item.img);
                  return <GenderGalleryItem key={item.id} filter={item.filter} img={item.img} title={item.title} />
               })}
            </div>
            <div onClick={() => han('001', 'likes', 's,flrfglw0496')} class="gallery__btn">
               <p class="gallery__link">Купить</p>
            </div>
         </div>
      </div>
   )
}
export default GenderGallery
