import React, { useState, useContext } from "react";
import { NavLink } from "react-router-dom";
import { PagesContext } from "../Context/PagesProvider";
function CatalogeFilter({ filtres, name }) {
   const [scoor, setScoor] = useState(0)
   const { filterCheckBox, setFilterCheckBox } = useContext(PagesContext)
   console.log(filterCheckBox);
   function handlerClick(event) {
      if (filterCheckBox == name) {
         setFilterCheckBox(null)
      }
      else if (filterCheckBox != name) {
         setFilterCheckBox(name)
      }
   }

   const stylesOptions = {
      display: filterCheckBox == name ? "block" : "none"
   }
   return (
      <li class="filter__list-item">
         <div onClick={handlerClick} class="filter__item">
            <p>{name}</p>
            <div style={stylesOptions} class="filter__option">
               <div class="filter__option-filter">
                  <div class="filter__option-block">
                     <p class="filter__option-span">Выбрано: <span>{scoor}</span></p>
                  </div>
                  <input class="filter__option-btn" type="button" value="Выбрать" />
               </div>
               <ul class="filter__option-list">
                  {filtres.map((item, index) => {
                     return <li key={index} class="filter__option-item">
                        <p class="filter__option-link">{item}</p>
                     </li>
                  })}
               </ul>
            </div>
         </div>
      </li>
   )
}

export default CatalogeFilter
