import React from "react";
import axios from "axios"
export const GetAxios = async (url) => {
   const response = await axios.get(`http://localhost:3000/${url}`)
   return response.data
}

export const PostAxios = async (url, body) => {
   const response = await axios.post(`http://localhost:3000/${url}`, body)
   return response.data
}
export const PatchAxios = async (url, body) => {
   const response = await axios.patch(`http://localhost:3000/${url}`, body)
   return response
}
export const PutAxios = async (url, body) => {
   const response = await axios.put(`http://localhost:3000/${url}`, body)
   return response
}
export const bodyCheck = async (key, body) => {
   if (key == 'basket') {
      return {
         basket: body
      }
   }
   else if (key == 'likes') {
      return {
         likes: body
      }
   }
   else if (key == 'order') {
      return {
         order: body
      }
   }
}


export async function ItemChange(userID, basLikOrd, delProductID) {
   const response = await GetAxios(`UserChange?user_id=${userID}`) //get
   const respChange = await response[0][`${basLikOrd}`] // если Change если user то response[0]
   const filterChange = respChange.filter((item) => item.id != delProductID) // если удалить выбераем конкретный обьект в массиве
   const body = await bodyCheck(basLikOrd, filterChange) //+/ ключи нужно проверять перебором if другого способа не вижу
   const responsePATCH = await PatchAxios(`UserChange/${response[0].id}`, body) // обычный PATCH просто меняеться BODY
   console.log(responsePATCH);
}

export async function add(userID, basLikOrd, bodyAdd) {
   const response = await GetAxios(`UserChange?user_id=${userID}`) //get
   const respChange = await response[0][`${basLikOrd}`]
   await respChange.push(bodyAdd)
   const body = await bodyCheck(basLikOrd, respChange)
   const responsePATCH = await PatchAxios(`UserChange/${response[0].id}`, body) // обычный PATCH просто меняеться BODY
   console.log(responsePATCH);

}




