import React, { useState } from "react";
import HeaderNavSort from "./HeaderNavSort";
function HeaderNavList() {
   const [category, setCategory] = useState(["Распродажа", "Новинки", "Одежда", "Обувь", "Модно в этом году",
      "Бренды", "Всё"])
   return (
      <div>
         <nav class="nav">
            <ul class="nav__list">
               {category.map((item) => {
                  <li class="nav__item">
                     <a href="#" class="nav__link">{item}</a>
                     <HeaderNavList />
                  </li>
               })}

            </ul>
         </nav>
      </div>
   )
}
export default HeaderNavList
