import React, { useState, useContext, useRef } from "react";
import { NavLink } from "react-router-dom";
import { PagesContext } from "../Context/PagesProvider";
function CatalogeFilterItem({ item, name }) {
   const [toggle, setToggle] = useState(false)
   const { brand, setBrand, category, setCategory, material, setMaterial, season,
      setSeason, basic, setBasic } = useContext(PagesContext)

   const setMass = name == "Категории" ? setCategory : name == "Основные" ? setBasic :
      name == "Сезон" ? setSeason : name == "Бренд" ? setBrand : setMaterial
   const mass = name == "Категории" ? category : name == "Основные" ? basic :
      name == "Сезон" ? season : name == "Бренд" ? brand : material
   const handler = () => {
      if (mass.filter((i) => i == item) == false) {
         console.log("no search");
         mass.push(item)
         setMass(mass)
      }
      else if (mass.filter((i) => i == item)) {
         setMass(mass.filter((i) => i != item))
      }

      if (!toggle) {
         setToggle(true)
      }
      else if (toggle) {
         setToggle(false)
      }
   }
   const styles = {
      backgroundColor: toggle ? 'blue' : 'inherit',
      border: toggle ? "1px solid #222" : '1px solid #222',
      borderRadius: ".7em",
   }
   return (
      <React.Fragment>
         <li onClick={handler} style={styles} className='filter__option-item'>
            <p class="filter__option-link">{item}</p>
         </li>

      </React.Fragment>

   )
}

export default CatalogeFilterItem
