import React, { useState } from "react";

function CatalogeFilterOptions({ filtres }) {
   const [scoor, setScoor] = useState(0)
   return (
      <div class="filter__option">
         <div class="filter__option-filter">
            <div class="filter__option-block">
               <p class="filter__option-span">Выбрано: <span>{scoor}</span></p>
            </div>
            <input class="filter__option-btn" type="button" value="Выбрать" />
         </div>
         <ul class="filter__option-list">
            {filtres.map((item) => {
               return <li class="filter__option-item">
                  <p class="filter__option-link">{item}</p>
               </li>
            })}
         </ul>
      </div>
   )
}

export default CatalogeFilterOptions
