   const [tabs, setTabs] = useState(null)
   const [scoor, setScoor] = useState(0)
   return (
      <PagesContext.Provider value={{ pageY0, tabs, setTabs, scoor, setScoor }}>
         {children}
      </PagesContext.Provider>
   )
}

export default PagesProvider
