import React, { useState, useEffect, useContext } from "react";
import { useRef } from 'react'
import { useNavigate } from "react-router-dom";
import { AccountContext } from "../Context/AccountProvider";
import { AuthContext } from "../Context/AuthProvider";

import { useForm } from 'react-hook-form'
function FormPost() {

   const {
      register,
      formState: { errors },
      handleSubmit
   } = useForm()

   const [errorReg, setErrorReg] = useState('')
   const getErrors = (str) => {
      setErrorReg(str)
      return <p>В поле <span>{str}</span> ошибка</p>
   }

   const useSubmitData = (data) => {
      alert(JSON.stringify(data))
   }

   return (
      <>
         <form onSubmit={handleSubmit(useSubmitData)} class="reg__form">
            <label class="reg__label" for="email">Адресс Электронной почты:
               <input class="reg__input" id="reg__emal" {...register("email", { required: true })} type="text" />
               <div className="reg__error">{errors.email && getErrors('Email')}</div>
            </label>
            <label class="reg__label" for="firstName">Имя:
               <input class="reg__input" id="reg__f-name" {...register("firstName", { required: true })} type="text" />
               <div className="reg__error">{errors.email && getErrors()}</div>
            </label>
            <label class="reg__label" for="lastName">Фамилия:
               <input class="reg__input" id="reg__l-name" {...register("lastName", { required: true })} type="text" />
               <div className="reg__error">{errors.email && getErrors()}</div>
            </label>
            <label class="reg__label" for="pass">Пароль:
               <input class="reg__input" id="reg__pass"  {...register("password", { required: true })} type="text" />
               <div className="reg__error">{errors.email && getErrors()}</div>
            </label>
            <button class="reg__btn" type="submit" >зарегестрироваться </button>
            <div className="reg__error">{errors && <p>В поле <span>{errorReg}</span> ошибка</p>}</div>
         </form>
      </>
   )
}
export default FormPost
