import React, { useEffect, useRef, useState } from "react";

function FormGet() {
   const form = useRef()
   const [user, setUser] = useState('')
   const [fetchChecked, setFetchChecked] = useState('')
   function HandlerButton() {
      const formElem = form.current
      const email = formElem.email.value
      const pass = formElem.pass.value
      setFetchChecked({
         email,
         pass
      })

   }
   useEffect(() => {
      fetch("http://localhost:3000/userCard")
         .then(data => data.json())
         .then(data => setUser(data))
         .then(data => {
            const searchEmail = user.filter((item) => item.email == fetchChecked.email)
            if (searchEmail.pass == fetchChecked.pass) {
               console.log("true pass");
            }
            console.log("seach: ", searchUser);
         })
   }, [fetchChecked])

   return (
      <>
         <form ref={form} class="vhod__form" action="">
            <label class="reg__label" for="email">Адресс Электронной почты:</label>
            <input class="reg__input" id="reg__emal" name="email" type="text" />
            <label class="reg__label" for="email">Пароль:</label>
            <input class="reg__input" id="reg__pass" name="pass" type="text" />
            <input onClick={HandlerButton} class="reg__btn" type="button" value="войти" />
         </form>
      </>
   )
}
export default FormGet
