   required: "Поле обязательно к заполнению",
   minLength: {
      value: 3,
      message: "Минимум 3 символов"
   }
}
const paramEmal = {
   required: "Поле обязательно к заполнению",
   pattern: {
      value: /^((([0-9A-Za-z]{1}[-0-9A-z\.]{1,}[0-9A-Za-z]{1})|([0-9А-Яа-я]{1}[-0-9А-я\.]{1,}[0-9А-Яа-я]{1}))@([-A-Za-z]{1,}\.){1,2}[-A-Za-z]{2,})$/u,
