import React, { useState, useContext, useRef } from "react";
import { NavLink } from "react-router-dom";
import { PagesContext } from "../Context/PagesProvider";
function CatalogeFilterItem({ item }) {
   const [toggle, setToggle] = useState(false)
   const handler = () => setToggle(!toggle)
   const styles = {
      backgroundColor: toggle ? 'blue' : 'inherit'
   }
   return (
      <React.Fragment>
         <li onClick={handler} style={styles} class='filter__option-item'>
            <p class="filter__option-link">{item}</p>
         </li>

      </React.Fragment>

   )
}

export default CatalogeFilterItem
