import React, { useContext, useEffect, useRef, useState } from "react";
import { PagesContext } from "../Context/PagesProvider";
import { useLocation, useNavigate, Outlet, NavLink } from 'react-router-dom'
import useAuth from "../../hook/useAuth"
function Registration() {
   //pageYo
   const { pageY0 } = useContext(PagesContext)
   useEffect(() => {
      pageY0()
   }, [])


   //navigate
   const location = useLocation()
   const navigate = useNavigate()
   const FromPage = location.state?.from?.pathname || "/"
   const [regulare, setRegulare] = useState(location.state.regulare || 'post')
   //auth sing in
   const { value } = useAuth()
   const singin = value.singin
   //e.preventDefault + form
   const btn = useRef()
   const emailRef = useRef()
   const HandlerButton = (event) => {
      singin(emailRef.current.value, () => navigate(FromPage, { replace: true }))
   }

   const [activePost, setActivePost] = useState('')
   const [activeGet, setActiveGet] = useState('')
   useEffect(() => {
      if (regulare == "post") {
         setActivePost('active')
         setActiveGet('')
      }
      else if (regulare == "get") {
         setActiveGet('active')
         setActivePost('')

      }
   }, [regulare])



   return (

      <div class="registration-body">
         <div class="reg">
            <div class="reg__title">
               <img src="/img/page-icon/vesh-logo.png" alt="" />
            </div>
            <div class="reg__block">
               <div class="reg__checkout">
                  <div onClick={() => setRegulare("post")} className={`reg__checkout-item ${activePost}`}>
                     <NavLink to='post'>
                        <p className='reg__checkout-link'>Регестрация</p>
                     </NavLink>
                  </div>
                  <div onClick={() => setRegulare("get")} className={`reg__checkout-item ${activeGet} `}>
                     <NavLink to='get'>
                        <p class="reg__checkout-link">Вход</p>
                     </NavLink>
                  </div>
               </div>
               <Outlet FromPage={FromPage} singin={singin} HandlerButton={HandlerButton} />
            </div>
         </div>

      </div>

   )
}
export default Registration

