import React, { useState, createContext, useEffect, useCallback } from "react";

export const AuthContext = createContext(null)

export function AuthProvider({ children }) {
   const [user, setUser] = useState(null)


   useEffect(() => {
      if (localStorage.getItem('auth') != null ||
         localStorage.getItem('auth') != undefined) {
         localStorage.setItem('auth', user);
         setUser(localStorage.getItem("auth"))
      }
   }, [user])

   const singIn = (us, Callback) => {
      localStorage.setItem("auth", us)
      Callback()
      setUser(localStorage.getItem("auth"))
   }
   const AuthOut = useCallback((Callback) => {
      setUser(null)
      localStorage.setItem('auth', null)

   }, [])

   return (
      <AuthContext.Provider value={{ user, AuthOut, singIn }}>
         {children}
      </AuthContext.Provider>
   )
}
