import React, { useEffect, useState, useRef } from "react";
import { Outlet } from 'react-router-dom'
import AccountNavigate from "./AccountNavigate";
function Account() {
   const [navigate, setNavigate] = useState([])

   useEffect(() => {
      fetch("http://localhost:3000/accountNavigate")
         .then(data => data.json())
         .then(data => setNavigate(data))
   }, [])


   const [margTop, setMargTop] = useState(0)
   console.log("margin: " + margTop);
   const cabNav = useRef()
   function scrolll(window) {
      setMargTop(99)
      console.log(margTop);
      console.log(window.scrollY);
      const paddTop = 10
      const localNavTen = cabNav.current.getBoundingClientRect().top + window.scrollY - paddTop
      if (localNavTen <= window.scrollY) {

         setMargTop(window.scrollY - localNavTen)
         console.log(margTop);
         //console.log(this.window.scrollY - localNavTen);
      }


   }
   /*useEffect(() => {
      window.addEventListener("scroll", function scroll() {
         const localNavTen = cabNav.current.getBoundingClientRect().top + this.window.scrollY - 10
         const pageY = this.window.scrollY
         const minus100 = pageY - localNavTen
         minus100 > 0 ? setMargTop(minus100) : setMargTop(0)
      }, true);
      return () => {
         window.removeEventListener("scroll", function scroll() {
            const localNavTen = cabNav.current.getBoundingClientRect().top + this.window.scrollY - 10
            const pageY = this.window.scrollY
            const minus100 = pageY - localNavTen
            minus100 > 0 ? setMargTop(minus100) : setMargTop(0)

         }, true);
      }
   }, [])*/

   useEffect(() => {
      window.addEventListener("scroll", function scroll() {
         const localNavTen = cabNav.current.getBoundingClientRect().top + document.documentElement.scrollTop - 10
         const pageY = document.documentElement.scrollTop
         const minus100 = pageY - localNavTen
         console.log(pageY);

         minus100 > 0 ? setMargTop(minus100) : setMargTop(0)
      }, true);
      return () => {
         window.removeEventListener("scroll", function scroll() {
            const localNavTen = cabNav.current.getBoundingClientRect().top + document.documentElement.scrollTop - 10
            const pageY = document.documentElement.scrollTop
            const minus100 = pageY - localNavTen

            console.log(pageY);
            minus100 > 0 ? setMargTop(pageY) : setMargTop(0)

         }, true);
      }
   }, [])
   const styleNav = {
      marginTop: `calc(${margTop}px - 100px)`
   }

   return (
      <>
         <div class="registration-body">
            <div class="cab">
               <div class="cab__block">
                  <div class="cab__nav-title">
                     <img src="/img/page-icon/vesh-logo2.png" alt="lojgo" />
                  </div>
                  <div class="cab__supp-title">
                     <h1>Личный кабинет</h1>
                  </div>
               </div>
               <div className="cab__block">
                  <div ref={cabNav} style={styleNav} className="cab__nav">
                     <nav class="cab__nav-nav">
                        <div class="cab__nav-face">
                           <div class="cab__ava">
                              <img src="/img/page-icon/delivery.svg" alt="" class="cab__ava-item" />
                           </div>
                           <div class="cab__ava-text">
                              <p id="ava__f-name">Даниил</p>
                              <p id="ava__l-name">Осипов(ввввввввв)</p>
                           </div>
                        </div>
                        <ul class="cab__list">
                           {navigate.map((link) => {
                              return (<AccountNavigate key={link.id} link={link} />)
                           })}
                        </ul>
                     </nav>
                  </div>
                  <div class="cab__supp">
                     <Outlet />
                  </div>
               </div>
            </div>
         </div>

      </>
   )
}
export default Account
