import React from "react";
import { useState } from "react";
export const PagesContext = React.createContext()

function PagesProvider({ children }) {
   const pageY0 = () => document.documentElement.scrollTop = 0
   const [tabs, setTabs] = useState(null)
   const [brand, setBrand] = useState([])
   const [category, setCategory] = useState([])
   const [material, setMaterial] = useState([])
   const [season, setSeason] = useState([])
   const [basic, setBasic] = useState([])
   const massFilters = [brand, category, material, season, basic]
   const [finishFilter, setFinishFilter] = useState([])// ===  [  [...], [...], [...]   ]
   function searchForFilter(products) {
      setFinishFilter([])
      if (brand.length) {
         brand.map((item) => {
            setFinishFilter(products.filter((prod) => prod.brand == item))
         })
      }
      else if (category.length) {
         if (finishFilter.length) {
            category.map((item) => {
               setFinishFilter(finishFilter.filter((prod) => prod.filter_name == item))
            })
         }
         else {
            const massiv = []
            category.map((item) => {
               massiv.push(products.filter((prod) => prod.filter_name == item)[0])
            })
            setFinishFilter(massiv)
         }
      }
      else if (material.length) {
         if (finishFilter.length) {
            material.map((item) => {
               setFinishFilter(finishFilter.filter((prod) => prod.material == item))
            })
         }
         else {
            material.map((item) => {
               setFinishFilter(products.filter((prod) => prod.material == item))
            })
         }
      }
      else if (season.length) {
         if (finishFilter.length) {
            season.map((item) => {
               setFinishFilter(finishFilter.filter((prod) => prod.season == item))
            })
         }
         else {
            season.map((item) => {
               setFinishFilter(products.filter((prod) => prod.season == item))
            })
         }
      }
      else if (basic.length) {
         if (finishFilter.length) {
            basic.map((item) => {
               if (item == 'sales') setFinishFilter(finishFilter.filter((prod) => prod.sales == true))
               else if (item == 'moda') setFinishFilter(finishFilter.filter((prod) => prod.moda == true))
               else if (item == 'news') setFinishFilter(finishFilter.filter((prod) => prod.news == true))
            })
         }
         else {
            basic.map((item) => {
               if (item == 'sales') setFinishFilter(products.filter((prod) => prod.sales == true))
               else if (item == 'moda') setFinishFilter(products.filter((prod) => prod.moda == true))
               else if (item == 'news') setFinishFilter(products.filter((prod) => prod.news == true))
            })
         }
      }


   }
   return (
      <PagesContext.Provider value={{
         finishFilter,
         brand, setBrand, category, setCategory, material, setMaterial, season,
         setSeason, basic, setBasic, searchForFilter, pageY0, tabs, setTabs
      }}>
         {children}
      </PagesContext.Provider>
   )
}

export default PagesProvider
